var button=document.querySelector(".search-button"),popup=document.querySelector(".modal-search"),form=document.querySelector("form"),arrival=document.querySelector("[name=arrival-date]"),departure=document.querySelector("[name=departure-date]"),adults=document.querySelector("[name=adults]");button.addEventListener("click",function(e){e.preventDefault(),popup.classList.toggle("modal-search-show"),arrival.focus(),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(e){arrival.value&&departure.value&&adults.value||(e.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("modal-search-show")&&(popup.classList.remove("modal-search-show"),popup.classList.remove("modal-error")))});